mapper.select:
SELECT user_no
FROM users
WHERE user_id = #{userId}

mapper.update:
UPDATE users
   SET user_id = #{userId}
      ,user_pw = #{userPw}
      ,name = #{name}
      ,phone = #{phone}
      ,email = #{email}
      ,address = #{address}
WHERE user_id = #{userId}

mapper.delete:
DELETE users, auth
FROM users JOIN auth ON users.user_id = auth.user_id        
WHERE users.user_id = #{userId} AND auth.user_id = #{userId}

mapper.select:
SELECT mileage
FROM mileage
WHERE user_id = #{userId}

SELECT *
FROM cart
WHERE phone=#{phone} AND user_pw = #{userPw}
